mapper.select:
SELECT B.* FROM(
	SELECT rownum rn, A.* FROM( 
		SELECT * FROM member 


				WHERE memberId like '%'||#{search}||'%'


				WHERE nameKR like '%'||#{search}||'%'


				WHERE nameENG like '%'||#{search}||'%'


				WHERE mobile like '%'||#{search}||'%'


				WHERE email like '%'||#{search}||'%'


	)A
)B WHERE rn BETWEEN #{b} and #{e}
