mapper.select:
SELECT count(*) FROM payment

	hotelId=#{hotelId}
	AND paymentDate BETWEEN #{startDate} AND #{endDate}
	AND paymentType=#{paymentType}
	AND paymentStatus=#{paymentStatus}
	AND memberId like '%'||#{memberId}||'%'

SELECT B.* FROM(
	SELECT rownum rn, A.* FROM( 
		SELECT * FROM payment

		hotelId=#{hotelId}
		AND paymentDate BETWEEN #{startDate} AND #{endDate}
		AND paymentType=#{paymentType}
		AND paymentStatus=#{paymentStatus}
		AND memberId like '%'||#{memberId}||'%'

	ORDER BY paymentDate DESC, paymentNo DESC
		)A
	)B WHERE rn BETWEEN #{b} and #{e}

SELECT sum(paymentAmount) FROM payment

	hotelId=#{hotelId}
	AND paymentDate BETWEEN #{startDate} AND #{endDate}
	AND paymentType=#{paymentType}
	AND paymentStatus=#{paymentStatus}
	AND memberId like '%'||#{memberId}||'%'

mapper.update:
UPDATE payment
SET paymentStatus = #{paymentStatus}
WHERE paymentNo = #{paymentNo}
