mapper.select:
SELECT B.* FROM(
	SELECT rownum rn, A.* FROM( 
		SELECT * FROM reservation 

			hotelId=#{hotelId}

			AND reservationDate BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND to_date(#{endDate},'YYYY-MM-DD')

			AND checkinDate BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND to_date(#{endDate},'YYYY-MM-DD')
			AND reservationNo like '%'||#{reservationNo}||'%'

		ORDER BY reservationDate DESC, checkinDate ASC
	)A
)B WHERE rn BETWEEN #{b} and #{e}

mapper.update:
UPDATE reservation
SET breakfastCheck = #{breakfastCheck}
WHERE reservationNo = #{reservationNo}

UPDATE reservation
SET reservationStatus = #{reservationStatus}, cancelDate = to_date(#{cancelDate},'YYYY-MM-DD')
WHERE reservationNo = #{reservationNo}

UPDATE reservation
SET reservationStatus = #{reservationStatus}
WHERE reservationNo = #{reservationNo}
