mapper.select:
SELECT count(*) FROM reservation 



			hotelId=#{hotelId}



			reservationDate BETWEEN #{startDate} AND #{endDate}



			checkinDate BETWEEN #{startDate} AND #{endDate}


			reservationNo like '%'||#{reservationNo}||'%'

SELECT B.* FROM(
	SELECT rownum rn, A.* FROM( 
		SELECT * FROM reservation 



			hotelId=#{hotelId}



			reservationDate BETWEEN #{startDate} AND #{endDate}



			checkinDate BETWEEN #{startDate} AND #{endDate}


			reservationNo like '%'||#{reservationNo}||'%'



	)A
)B WHERE rn BETWEEN #{b} and #{e}
