mapper.select:
SELECT count(*) FROM reservation 


		WHERE (reservatinDate BETWEEN #{startDt} AND #{endDt}) AND memberId == #{memberId}


		WHERE checkinDate BETWEEN #{startDt} AND #{endDt} AND memberId == #{memberId}

SELECT D.* FROM(
	SELECT rownum rn, C.* FROM( 
		SELECT B.*, (SELECT A.hotelName FROM hotel A WHERE A.hotelId = B.hotelId) AS hotelName
		FROM reservation B


				WHERE (reservatinDate BETWEEN #{startDt} AND #{endDt}) AND memberId = #{memberId}


				WHERE checkinDate BETWEEN #{startDt} AND #{endDt} AND memberId = #{memberId}


	)C
)D WHERE rn BETWEEN #{b} and #{e}
